import axios from 'axios';
import React, { useEffect, useState } from 'react';

function ToScript() {
    const [result, setResult] = useState(null);

    useEffect(() => {
      axios.get('https://pptforscript.s3.amazonaws.com/sample.pptx', { responseType: 'blob' })
        .then(response => {
          const reader = new FileReader();
          reader.onload = () => {
            const pptxData = reader.result;
            // 여기서 파이썬 파일을 불러와 함수를 실행하고, 결과를 setResult에 저장합니다.
          };
          reader.readAsArrayBuffer(response.data);
        })
        .catch(error => {
          console.error(error);
        });
    }, []);

    const python = require('python');

    const convertToScript = async (file) => {
        const pythonFile = new python.Python();
        const script = pythonFile.run('PPTtoScript.py');
        const result = await script.func('convert_to_script', file);
        return result;
    };

    return (
      <div>
        {result && <p>{result}</p>}
      </div>
    );
}

export default ToScript;
