import React, { useEffect, useState } from 'react';

function ToScript() {
    const [result, setResult] = useState(null);
    const pyscript = require('./PPTtoScript.py');

    useEffect(() => {
        pyscript.PtoS('./sample.pptx')
            .then(result => {
                setResult(result);
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    return(
        <div>
            {result && <p>{result}</p>}
        </div>
    )
}

export default ToScript;
