import AWS from "aws-sdk";
import PyScript, { PyScriptProvider } from "pyscript-react";
import React from 'react';
import zustand from 'zustand';

//import { spawn } from "child_process";

function ToScript() {
    const fs = require('fs');
    const AWS = require('aws-sdk');
    const S3_BUCKET = 'pptforscript';
    const REGION = "ap-southeast-2";
    const s3 = new AWS.S3({
        accessKeyId: "AKIAU4PAKJFYHKHYQX6A",
        secretAccessKey: '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'
    });

    /*
    const downloadFile = (fileName) => {
        const params = {
            Bucket: S3_BUCKET,
            Key: 'sample.pptx'
        };
        s3.getObject(params, function(err, data) {
            if(err) {
                throw err;
            }
            fs.writeFileSync(fileName, data.Body.toString());
        });
    };
    */
    const getFile = async (bucketName, fileName) => {
        const params = {
          Bucket: S3_BUCKET,
          Key: 'sample.pptx'
        };
        const file = await s3.getObject(params).promise();
        return file.Body;
    };

    return(
        <div>
            <PyScriptProvider>
                <PyScript>
                    source='/PPTtoScript.py',
                    output='script',
                    generateOutputTag
                    pyConfigProps={{
                    type: "json",
                    packages: new Set(["folium"]),
                    }}
                </PyScript>
            </PyScriptProvider>
        </div>
    )
}

export default ToScript;
