import AWS from "aws-sdk";
import fs from "fs";

function ToScript() {
    //const spawn = require('child_process').spawn;
    const { spawn } = require('child_process');
    const result = spawn('python', ['PPTtoScript.py', './sample.pptx']);

    const fs = require('fs');
    const AWS = require('aws-sdk');
    const S3_BUCKET = 'pptforscript';
    const REGION = "ap-southeast-2";
    const s3 = new AWS.S3({
        secretAccessKey: 'jYNF3dkk06e44cL6iP7aNk9WBsjNOlkgxwNRKfQ1'
    });

    /*
    const downloadFile = (fileName) => {
        const params = {
            Bucket: S3_BUCKET,
            Key: 'sample.pptx'
        };
        s3.getObject(params, function(err, data) {
            if(err) {
                throw err;
            }
            fs.writeFileSync(fileName, data.Body.toString());
        });
    };
    */

    const getFile = async (bucketName, fileName) => {
        const params = {
          Bucket: S3_BUCKET,
          Key: 'sample.pptx'
        };
        const file = await s3.getObject(params).promise();
        return file.Body;
    };

    result.stderr.on('data', function(data) {
        console.log(data.toString());
    });

    result.stdout.on('data', (result)=> {
        console.log(result.toString());
    });

    return(
        <div></div>
    )
}

export default ToScript;
