import * as React from 'react';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const defaultTheme = createTheme();

export default function SignUpPage() {
  const [loginId, setLoginId]=React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [passwordCheck, setPasswordCheck] = React.useState('');
  const [nickname, setNickname] = React.useState('');
  const [birthYear, setBirthYear ] = React.useState('');
  const [birthMonth, setBirthMonth ] = React.useState('');
  const [birthDay, setBirthDay] = React.useState('');
  const [job, setJob] = React.useState('');
  const [showPolicyPopup, setShowPolicyPopup] = useState(false);

  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();

    if(!agreePolicy || !agreeInfoCollection){
      alert('필수 항목에 동의해야 합니다.');
      return;
    }

    fetch('/jwt-api-login/join',{
      method:'POST',
      headers:{
        'Content-Type':'application/json',
      },
      body:JSON.stringify({nickname,email,loginId,password,passwordCheck,birthYear,birthMonth,birthDay,job,gender,agreePolicy:agreePolicy ? 'Y' : 'N',agreeInfoCollection:agreeInfoCollection ? 'Y' : 'N',agreePromotionEmails:agreePromotionEmails ? 'Y' : 'N'}),
    })
      .then((response)=>response.text())
      .then((data)=>{
        console.log(data);
        if (data === "로그인 아이디가 중복됩니다.") {
          alert("로그인 아이디가 중복됩니다.");
        } else if (data === "이메일이 중복됩니다.") {
          alert("이메일이 중복됩니다.");
        } else if (data === "닉네임이 중복됩니다.") {
          alert("닉네임이 중복됩니다.");
        } else if (data === "비밀번호가 일치하지 않습니다.") {
          alert("비밀번호가 일치하지 않습니다.");
        } else if (data === "회원가입 성공") {
          alert("정상적으로 회원등록이 되었습니다!");
          navigate('/SignIn');
        }
      })
      .catch((error)=>{
        console.error(error);
      });

  };
  const handleId = (event) =>{
    event.preventDefault();
  fetch('/jwt-api-login/check-id', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      loginId: loginId,
    }),
  })
    .then((response) => response.json())
    .then((data) => {
      if (data.available) {
        alert('사용할 수 없는 아이디입니다. 다른 아이디를 입력해주세요.');
      } else {
        alert('사용 가능한 아이디입니다.');
      }
    })
    .catch((error) => {
      console.error(error);
    });
  }
  const handleEmail = (event) =>{
    event.preventDefault();
  fetch('/jwt-api-login/check-email', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      email: email,
    }),
  })
    .then((response) => response.json())
    .then((data) => {
      if (data.available) {
        alert('중복된 이메일입니다. 다른 이메일을 입력해주세요.');
      } else {
        alert('사용 가능한 이메일입니다.');
      }
    })
    .catch((error) => {
      console.error(error);
    });
  }
  const handlePasswordCheck = (event) => {
    event.preventDefault();

    if (password !== passwordCheck) {
      alert('비밀번호가 일치하지 않습니다.');
      return;
    } else {
      alert('비밀번호가 확인되었습니다.')
    }
  };
  const [gender, setGender] = useState('');

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const [agreePolicy, setAgreePolicy] = useState(false);
  const [agreeInfoCollection, setAgreeInfoCollection] = useState(false);
  const [agreePromotionEmails, setAgreePromotionEmails] = useState(false);

  const handleAgreePolicyChange = (event) => {
    setAgreePolicy(event.target.checked);
    setAgreeInfoCollection(event.target.checked);
    setAgreePromotionEmails(event.target.checked);
  };

  const termsOfServiceContent = `
  약관 동의 내용을 여기에 작성합니다. 이용약관은 사용자가 서비스를 이용하는 데 필요한 규정과 권리, 의무 등을 명시합니다.
  `;

  const privacyPolicyContent = `
  개인 정보 수집 및 이용 동의 내용을 여기에 작성합니다. 개인 정보 보호와 관련된 사항 및 수집된 정보의 활용 방법 등이 포함됩니다.
  `;

  const promotionEmailsContent = `
  이벤트 등 프로모션 메일 수신 동의 내용을 여기에 작성합니다. 사용자가 프로모션 이메일을 받는 것에 대한 동의와 관련된 내용입니다.
  `;

  // 각 약관 팝업 표시 함수
  const showTermsOfServicePopup = () => {
    setShowPolicyPopup(true);
  };

  const showPrivacyPolicyPopup = () => {
    setShowPolicyPopup(true);
  };

  const showPromotionEmailsPopup = () => {
    setShowPolicyPopup(true);
  };

  // 팝업 닫기 함수
const closePolicyPopup = () => {
  setShowPolicyPopup(false);
};


  return (
    <div>
        {/* <ThemeProvider theme={defaultTheme}> */}
      {/* <div> */}
        <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
        <div class = "pageDiv">
            <div class ="pageTitle" style={{marginTop:'50px'}}>
                회원가입
            </div>
            <div class ="pageAddScript" >
                편리함. 그 이상의 능력에 빠질 준비
            </div>
        </div>
        <div className='signUpDataAll'>
          <Box component="form"  onSubmit={handleSubmit} noValidate
          sx={{ mt: 3, alignItems: 'center',
                        "& fieldset": {borderTopColor:'white',
                                        borderRightColor:'white',
                                        borderLeftColor:'white',
                                        borderBottomColor:'black',
                                        borderRadius:0
                                        } }}>
            <div className="setData">
                <Grid container spacing={2}>
                <Grid item xs={10}>
                    <TextField
                    autoComplete="username"
                    name="loginId"
                    required
                    fullWidth
                    id="loginId"
                    label="ID"
                    value={loginId}
                    onChange={(e)=>setLoginId(e.target.value)}
                    autoFocus
                    />
                </Grid>
                <button className='authenBtn' type="submit" onClick={handleId}>확인</button>
                <Grid item xs={10}>
                    <TextField
                    required
                    fullWidth
                    id="email"
                    label="EMAIL"
                    name="email"
                    autoComplete="email"
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}
                    />
                </Grid>
                <button className='authenBtn' type="submit" onClick={handleEmail}>확인</button>
                <Grid item xs={12}>
                    <TextField
                    required
                    fullWidth
                    id="password"
                    label="Password"
                    name="password"
                    type='password'
                    autoComplete="new-password"
                    value={password}
                    onChange={(e)=>setPassword(e.target.value)}
                    />
                </Grid>
                <Grid item xs={10}>
                    <TextField
                    required
                    fullWidth
                    name="passwordCheck"
                    label="Password Check"
                    type="password"
                    id="passwordCheck"
                    value={passwordCheck}
                    onChange={(e)=>setPasswordCheck(e.target.value)}
                    />
                </Grid>
                <button className='authenBtn' type="submit" onClick={handlePasswordCheck}>확인</button>

                </Grid>
            </div>
            <div className="middleScript">개인정보</div>
            <div className="userData">
                <Grid container spacing={2}>
                <Grid item xs={2} >이름</Grid>
                <Grid item xs={10}>
                    <TextField
                    autoComplete="given-name"
                    name="nickname"
                    required
                    fullWidth
                    id="nickname"
                    label=""
                    autoFocus
                    type="nickname"
                    value={nickname}
                    onChange={(e)=>setNickname(e.target.value)}
                    />
                </Grid>
                <Grid item xs={2} >성별</Grid>
                <Grid>
                <div className="select">
                <input
                  type="radio"
                  id="select"
                  name="gender"
                  value="Male"
                  checked={gender === 'Male'}
                  onChange={handleGenderChange}
                />
                <label htmlFor="select" className={gender === 'Male' ? "selected" : ""}>남성</label>
                <input
                  type="radio"
                  id="select2"
                  name="gender"
                  value="Female"
                  checked={gender === 'Female'}
                  onChange={handleGenderChange}
                />
                <label htmlFor="select2" className={gender === 'Female' ? "selected" : ""}>여성</label>

              </div>
                </Grid>
                <div>

                </div>
                <Grid container justifyContent="center" alignItems="center">
                  생년월일
                  <Grid item xs={12} sm={3}>
                    <TextField
                      required
                      fullWidth
                      id="birth_year"
                      label=""
                      name="birth_year"
                      value={birthYear}
                      onChange={(e)=>setBirthYear(e.target.value)}
                    />

                  </Grid>년
                  <Grid item xs={12} sm={2}>
                    <TextField
                      required
                      fullWidth
                      id="birth_month"
                      label=""
                      name="birth_month"
                      value={birthMonth}
                      onChange={(e)=>setBirthMonth(e.target.value)}
                    />

                  </Grid>월
                  <Grid item xs={12} sm={2}>
                    <TextField
                      required
                      fullWidth
                      id="birth_day"
                      label=""
                      name="birth_day"
                      value={birthDay}
                      onChange={(e)=>setBirthDay(e.target.value)}
                    />

                  </Grid>일 
                </Grid>
                <Grid container justifyContent="center" alignItems="center" marginTop={'45px'} paddingLeft={'13px'} marginBottom={'50px'}>
                  직업
                  <Grid item xs={10}>
                  <select name="job" id="" class="jobSelect" onChange={(e) => setJob(e.target.value)}>
                    <option value="0" selected>현재 직업을 선택하세요</option>
                    <option value="elementary">초등학생</option>
                    <option value="middle">중학생</option>
                    <option value="high">고등학생</option>
                    <option value="college">대학생</option>
                    <option value="professor">교수</option>
                    <option value="worker">직장인</option>
                    <option value="worker">프리랜서</option>
                    <option value="worker">주부</option>
                    <option value="worker">기타</option>
                  </select> 
                  </Grid>
                </Grid>



                </Grid>
            </div>
            <div className='middleScript'>이용약관</div>
            <div className='policy'>
                <div className='policyContent'>
                  <div class="custom-checkbox">
                    <input type="checkbox" id="agreePolicy" checked={agreePolicy} onChange={(e) => setAgreePolicy(e.target.checked)}/>
                    <label for="agreePolicy">이용약관 동의 (필수)</label>
                    <button onClick={() => setShowPolicyPopup}>약관 보기</button>
                  </div>
                </div>
                <div className='policyContent'>
                  <div class="custom-checkbox">
                    <input type='checkbox' id='agreeInfoCollection' checked={agreeInfoCollection} onChange={(e) => setAgreeInfoCollection(e.target.checked)}/>
                    <label for='agreeInfoCollection'>개인 정보 수집 및 이용 동의 (필수)</label>
                    <button onClick={showPrivacyPolicyPopup}>약관 보기</button>
                  </div>
                </div>
                <div className='policyContent'>
                  <div class='custom-checkbox'>
                    <input type='checkbox' id='agreePromotionEmails' checked={agreePromotionEmails} onChange={(e) => setAgreePromotionEmails(e.target.checked)}/>
                    <label for='agreePromotionEmails'>이벤트 등 프로모션 메일 수신 동의 (선택)</label>
                    <button onClick={showPrivacyPolicyPopup}>약관 보기</button>
                  </div>

            </div>
            {showPolicyPopup && (
  <div className="modal">
    {showTermsOfServicePopup && (
      <div className="modal-content">
        <h2>이용약관</h2>
        <p>{termsOfServiceContent}</p>
        <button onClick={closePolicyPopup}>닫기</button>
      </div>
    )}

    {showPrivacyPolicyPopup && (
      <div className="modal-content">
        <h2>개인 정보 수집 및 이용 동의</h2>
        <p>{privacyPolicyContent}</p>
        <button onClick={closePolicyPopup}>닫기</button>
      </div>
    )}

    {showPromotionEmailsPopup && (
      <div className="modal-content">
        <h2>이벤트 등 프로모션 메일 수신 동의</h2>
        <p>{promotionEmailsContent}</p>
        <button onClick={closePolicyPopup}>닫기</button>
      </div>
    )}
  </div>
)}
            </div>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2,
                backgroundColor: '#4982F7',
                fontFamily: 'Noto Sans KR',
                borderRadius:2,
                boxShadow:5,
                textDecorationStyle:'bold',
                marginBottom: 50
            }}
            >
              회원가입
            </Button>
            {/* <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="#" variant="body2">
                  계정이 이미 있으신가요? 로그인하러 가기
                </Link>
              </Grid>
            </Grid> */}
            </Box>
          </div>
        </Box>
        {/* <Copyright sx={{ mt: 5 }} /> */}
      {/* </div> */}
      </Container>
    </div>
  );
}
