import React, { useEffect, useState } from 'react';
import aws from 'aws-sdk';
import pyscript from 'pyscript-react';

const ToScript = () => {
    const [file, setFile] = useState(null);
    const [result, setResult] = useState(null);
    const pyscript = require('./PPTtoScript.py');

    useEffect(() => {
        const s3 = new aws.S3({
            accessKeyId: 'AKIAU4PAKJFYHKHYQX6A',
            secretAccessKey: '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',
            region: 'ap-southeast-2'
        });

        const params = {
            Bucket: 'pptforscript',
            Key: 'sample.pptx'
        };

        s3.getObject(params, function(err, data) {
            if (err) {
                console.log('s3 연결 실패');
                console.log(err, err.stack);
            } else {
                console.log('s3 연결 성공');
                setFile(data.Body);
            }
        });
    }, []);

    useEffect(() => {
        if (file) {
            pyscript.PtoS(file)
                .then(result => {
                    console.log('함수 호출 성공');
                    setResult(result);
                })
                .catch(error => {
                    console.log('함수 호출 실패');
                    console.error(error);
                });
        }
        else {
            console.log('파일 없음')
        }
    }, [file]);

    return(
        <div>
            <p>{file}</p>
            <p>{result}</p>
        </div>
    )
}

export default ToScript;
