//PPT->Script 과정에서 PPT 업로드하는 페이지
import AWS from "aws-sdk";
import { useState } from "react";

function PPTUpload() {
  // Create state to store file
  const [file, setFile] = useState(null);

  // Function to upload file to s3
  const uploadFile = async () => {
    // S3 Bucket Name
    const S3_BUCKET = "pptforscript";

    // S3 Region
    const REGION = "ap-southeast-2";

    // S3 Credentials
    AWS.config.update({
      accessKeyId: "AKIAU4PAKJFYHKHYQX6A",
      secretAccessKey: "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",
    });
    const s3 = new AWS.S3({
      params: { Bucket: S3_BUCKET },
      region: REGION,
    });

    // Files Parameters

    const params = {
      Bucket: S3_BUCKET,
      Key: file.name,
      Body: file,
    };

    // Uploading file to s3

    var upload = s3
      .putObject(params)
      .on("httpUploadProgress", (evt) => {
        // File uploading progress
        console.log(
          "Uploading " + parseInt((evt.loaded * 100) / evt.total) + "%"
        );
      })
      .promise();

    await upload.then((err, data) => {
      console.log(err);
      // Fille successfully uploaded
      alert("File uploaded successfully.");
      //navigate("/");
    });
  };
  // Function to handle file and store it to file state
  const handleFileChange = (e) => {
    // Uploaded file
    const file = e.target.files[0];
    // Changing file state
    setFile(file);
  };
  return (
    <div>
      <p class='pageTitle'>PPT to Script</p>
      <p class='pageAddScript'>버튼 하나, 나의 든든한 서포터</p> 
      <p style={{ color: 'lightgray', marginTop: '100px'}}>PPT 업로드</p>

      <div style={{ display: 'flex', alignItems: 'center' }}>
          <div className="ppt-upload-box">
              <p>"이곳에 파일을 드래그하세요."</p>
          </div>

          <input type="file" onChange={handleFileChange} />
      </div>

      <a
        href="#"
        style={{ color: '#4982F7', textDecoration: 'underline' }}
      >
        워크스페이스에서 찾기
      </a>

      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '60px'}}>
      <button onClick={uploadFile}>Upload</button>
      </div>
    </div>
  );
}

export default PPTUpload;
